INSERT INTO `wp_snippets` VALUES (1,'Make upload filenames lowercase','Makes sure that image and file uploads have lowercase filenames.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.','add_filter( \'sanitize_file_name\', \'mb_strtolower\' );','sample, media','global',0,10,0,'2025-09-13 16:48:25',1,NULL),(2,'Disable admin bar','Turns off the WordPress admin bar for everyone except administrators.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.','add_action( \'wp\', function () {\n	if ( ! current_user_can( \'manage_options\' ) ) {\n		show_admin_bar( false );\n	}\n} );','sample, admin-bar','front-end',0,10,0,'2025-09-13 16:48:25',1,NULL),(3,'Allow smilies','Allows smiley conversion in obscure places.\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.','add_filter( \'widget_text\', \'convert_smilies\' );\nadd_filter( \'the_title\', \'convert_smilies\' );\nadd_filter( \'wp_title\', \'convert_smilies\' );\nadd_filter( \'get_bloginfo\', \'convert_smilies\' );','sample','global',0,10,0,'2025-09-13 16:48:25',1,NULL),(4,'Current year','Shortcode for inserting the current year into a post or page..\n\nThis is a sample snippet. Feel free to use it, edit it, or remove it.','<?php echo date( \'Y\' ); ?>','sample, dates','content',0,10,0,'2025-09-13 16:48:25',1,NULL),(5,'Resend Receipt API.','','// Resend Woo receipt (customer invoice) for a given order if the email matches the billing email.\n// Route: POST /wp-json/jontech/v1/resend-receipt/{orderId}\n// Body: { \"email\": \"customer@example.com\" }\n\nadd_action(\'rest_api_init\', function () {\n  register_rest_route(\'jontech/v1\', \'/resend-receipt/(?P<id>\\d+)\', array(\n    \'methods\'  => \'POST\',\n    \'callback\' => function (WP_REST_Request $req) {\n      $order_id = intval($req[\'id\']);\n      $email    = sanitize_email($req->get_param(\'email\'));\n\n      if (!$order_id || !$email) {\n        return new WP_Error(\'bad_request\', \'Missing order id or email\', array(\'status\' => 400));\n      }\n\n      if (!class_exists(\'WC_Order\')) {\n        return new WP_Error(\'woo_missing\', \'WooCommerce not loaded\', array(\'status\' => 500));\n      }\n\n      $order = wc_get_order($order_id);\n      if (!$order) {\n        return new WP_Error(\'not_found\', \'Order not found\', array(\'status\' => 404));\n      }\n\n      $billing_email = $order->get_billing_email();\n      if (!is_email($billing_email) || strcasecmp($billing_email, $email) !== 0) {\n        return new WP_Error(\'forbidden\', \'Email does not match order billing email\', array(\'status\' => 403));\n      }\n\n      // Trigger Customer Invoice (sends to billing email)\n      $emails = WC()->mailer()->get_emails();\n      $invoice_email = isset($emails[\'WC_Email_Customer_Invoice\']) ? $emails[\'WC_Email_Customer_Invoice\'] : null;\n      if (!$invoice_email) {\n        return new WP_Error(\'email_missing\', \'Customer Invoice email not available\', array(\'status\' => 500));\n      }\n\n      $invoice_email->trigger($order_id);\n\n      return array(\'ok\' => true, \'message\' => \'Email sent (Customer Invoice).\');\n    },\n    \'permission_callback\' => \'__return_true\', // simple guard: email must match billing email\n  ));\n});\n','','global',0,10,1,'2025-09-13 16:50:58',1,NULL),(6,'Reset Link  Frontend','','\n// Override the reset URL used in WPGraphQL Login\nadd_filter( \'graphql_user_reset_password_url\', function( $url, $user, $key ) {\n    \n    $frontend_url = \'http://localhost:5173/reset-password\';\n\n    // Build the new reset link with key + login params\n    $reset_link = add_query_arg( [\n        \'key\'   => $key,\n        \'login\' => rawurlencode( $user->user_login ),\n    ], $frontend_url );\n\n    return $reset_link;\n}, 10, 3 );\n','','global',0,10,1,'2025-09-13 19:42:45',1,NULL),(7,'Enable COD for WooGraphQL','','\n// Force-enable COD payment method for WooGraphQL Checkout\nadd_filter( \'woocommerce_graphql_payment_method_is_valid\', function( $is_valid, $payment_method ) {\n    if ( $payment_method === \'cod\' ) {\n        return true;\n    }\n    return $is_valid;\n}, 10, 2 );\n','','global',0,10,1,'2025-09-14 10:28:56',1,NULL);
